import React, { useCallback, useEffect, useRef, useState } from 'react'
import { Icon } from '@shopify/polaris';
import axios from 'axios'
import { Page, Card, DataTable } from '@shopify/polaris';
import { ProductsMinor } from '@shopify/polaris-icons';
import { InventoryMajor } from '@shopify/polaris-icons';
import { Select } from '@shopify/polaris';
import {Button} from '@shopify/polaris';


export default function Dashboard() {
    const from = useRef()
    const to = useRef();
    const[user , setUser] =useState()
    const[rows1 , setRows1 ]=useState([])
    const[count , setCount]=useState(1)
    const [selected, setSelected] = useState(10);
    const options1 = [
        { label: '10', value: '10' },
        { label: '20', value: '20' },
        { label:'30', value: '30' },
        { label:'40', value: '40' },
    ];

    const options = [
        { label: "Equals", value: "1" },
        { label: "Not Equals", value: "2" },
        { label: "Contains", value: "3" },
        { label: "Does Not Contains", value: "4" },
        { label: "Starts With", value: "5" },
        { label: "Ends With", value: "6" },
    ];

    const changepage=(e)=>{
        if(e.target.innerText ==="Prev"){
            if(count>2){
                setCount(count - 1)
            }
          }
          if(e.target.innerText==="Next"){
            if(count<Math.ceil(user/selected)){
                setCount(count + 1)
            }
          }
          to.current = selected * count;
          from.current = to.current - selected;
    }
    useEffect(()=>{
        to.current = selected * count;
        from.current = to.current - selected;
    },[])

    var rows = [
        [
            <Select
                onChange={selectfun}
                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />
        ]

    ];


    const handleSelectChange = useCallback((value) => {
        setSelected(value)

      },[]);

    useEffect(() => {
        const handler1 = async () => {
            rows.splice(1 , rows.length)   
           const response1 = await axios(` https://fbapi.sellernext.com/frontend/admin/getAllUsers?activePage=${count}&count=${selected}`, {
               headers: {
                   authorization: sessionStorage.getItem('token')
               }
           })
           setUser(response1.data.data.count)

           response1.data.data.rows.map((d) => {
               rows.push([d.user_id, d.catalog, d.username, d.email, d.shopify_plan, d.updated_at, d.created_at, d.shop_url])
           })
           setRows1(rows)
       }

        handler1();
        to.current = selected * count;
        from.current = to.current - selected;
    },[ selected , count])

 console.log(rows1)
    return (<>
    <div id="navbar_div_id">DASHBOARD</div>
        <div id="dashboard_products_main_div_id">

            <div id="inner_dashboard_div_1">
                <p className="p1"><Icon
                    source={InventoryMajor}
                    color="base"
                />Dashboard</p>
                <p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                />Products</p><p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                />Grid</p>
            </div>
            <div id="inner_dashboard_div_2">
                <h1 id="h1">
                    Data Grid
                </h1>
                <h2 id="h2">
                    Showing from {from.current} to {to.current} of {user} users
                </h2>
                <div>
                    <div id="buttons_and_select_div_id">
                        <div>
                            <Button id="btn1" onClick={changepage}>Prev</Button><span id="count_span_id">{count}</span><Button id="btn2" onClick={changepage}>Next</Button></div>
                        <div><Select
                        placeholder="Rows per page"
                        options={options1}
                        onChange={handleSelectChange}
                        value={selected}
                    />
                          </div>
                          <div>
                            <Button>View Coulumns</Button>
                          </div>
                                        </div>

                    <Page title="User Details">
                        <Card>
                            <DataTable
                                columnContentTypes={[
                                    'text',
                                    'numeric',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                ]}
                                headings={[
                                    'UserId',
                                    'Catalog',
                                    'Shop Domain',
                                    'Shop Email',
                                    'Shop Plan name',
                                    'Updated at',
                                    'Created at',
                                    'Shop myShopify Domain '

                                ]}
                                rows={rows1}
                            />
                        </Card>
                    </Page>
                </div>
            </div>
        </div>
        </>
    )
}
