import React, { useCallback, useEffect, useRef, useState } from 'react'
import { Icon } from '@shopify/polaris';
import axios from 'axios'
import { Page, Card, DataTable } from '@shopify/polaris';
import { ProductsMinor } from '@shopify/polaris-icons';
import { InventoryMajor } from '@shopify/polaris-icons';
import { Select } from '@shopify/polaris';
import {Button} from '@shopify/polaris';
import {TextField} from '@shopify/polaris';
import {Frame, Loading} from '@shopify/polaris';
import Selectfunction from './Selectfunction';


export default function Dashboard(props) {
    const[loading , setLoading]=useState(false)
    const[filter , setFilter]=useState(['1' , '1' , '1' ,'1' , '1' ,'1' ,'1' ,'1'])

    const from = useRef()
    const to = useRef();
    const[user , setUser] =useState()
    const[rows1 , setRows1 ]=useState([])
    const[count1 , setCount1]=useState(1)
    const [selected, setSelected] = useState(10);
    const options1 = [
        { label: '10', value: '10' },
        { label: '20', value: '20' },
        { label:'30', value: '30' },
        { label:'40', value: '40' },
    ];

    const changepage=(e)=>{
        if(e.target.innerText ==="Prev"){
            if(count1>2){
                setCount1(count1 - 1)
            }
          }
          if(e.target.innerText==="Next"){
            if(count1<Math.ceil(user/selected)){
                setCount1(count1 + 1)
            }
          }
          to.current = selected * count1;
          from.current = to.current - selected;
    }
    useEffect(()=>{
        to.current = selected * count1;
        from.current = to.current - selected;
    },[])

    var rows = [
        [ 
            [   <Selectfunction sel={filter[0]} sel1={setFilter} ind={0}/>,
                <TextField  placeholder="user_id" autoComplete="off"/> 
            ] ,
            [   <Selectfunction sel={filter[1]} sel1={setFilter} ind={0}/>,
                <TextField  placeholder="Catalog" autoComplete="off"/> 
            ] ,
            [   <Selectfunction sel={filter[2]} sel1={setFilter} ind={0}/>,
                <TextField  placeholder="user_id" autoComplete="off"/> 
            ] ,
            [
                <Selectfunction sel={filter[3]}/>,
                // <Select onChange={selectfun} options={options} value={filter} />,
                <TextField  placeholder="user_id" autoComplete="off"/> 
            ] ,
            [
                <Selectfunction  sel={filter[4]}/>,
                // <Select onChange={selectfun} options={options} value={filter} />,
                <TextField  placeholder="user_id" autoComplete="off"/> 
            ] ,
            [
                <Selectfunction sel={filter[5]}/>,
                // <Select onChange={selectfun} options={options} value={filter} />,
                <TextField  placeholder="user_id" autoComplete="off"/> 
            ] ,
            [
                <Selectfunction sel={filter[6]}/>,
                // <Select onChange={selectfun} options={options} value={filter} />,
                <TextField  placeholder="user_id" autoComplete="off"/> 
            ] ,
            [
                <Selectfunction sel={filter[7]}/>,
                // <Select onChange={selectfun} options={options} value={filter} />,
                <TextField  placeholder="user_id" autoComplete="off"/> 
            ] 


        ],
    ];


    const handleSelectChange = useCallback((value) => {
        setSelected(value)
      },[]);

    useEffect(() => {
        setLoading(true)
        const handler1 = async () => {
            rows.splice(1 , rows.length)   
           const response1 = await axios(` https://fbapi.sellernext.com/frontend/admin/getAllUsers?activePage=${count1}&count=${selected}`, {
               headers: {
                   authorization: sessionStorage.getItem('token')
               }
           })
           setUser(response1.data.data.count)
           response1.data.data.rows.map((d) => {
               rows.push([d.user_id, d.catalog, d.username, d.email, d.shopify_plan, d.updated_at, d.created_at, d.shop_url])
           })
           setRows1(rows)
           setLoading(false)
       }
        handler1();
        to.current = selected * count1;
        from.current = to.current - selected;
    },[ selected , count1])

    return (<>
    <div id="navbar_div_id">DASHBOARD</div>
        <div id="dashboard_products_main_div_id">

            <div id="inner_dashboard_div_1">
                <p className="p1"><Icon
                    source={InventoryMajor}
                    color="base"
                /><span>Dashboard</span></p>
                <p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                /><span>Products</span></p><p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                /><span>Grid</span></p>
            </div>
            <div id="inner_dashboard_div_2">
                {loading===true?<div style={{height: '100px'}}>
      <Frame>
        <Loading />
      </Frame>
    </div>:""}
                <h1 id="h1">
                    Data Grid
                </h1>
                <h2 id="h2">
                    Showing from {from.current} to {to.current} of {user} users
                </h2>
                <div>
                    <div id="buttons_and_select_div_id">
                        <div>
                            <Button id="btn1" onClick={changepage}>Prev</Button><span id="count_span_id">{count1}</span><Button id="btn2" onClick={changepage}>Next</Button></div>
                        <div><Select
                        placeholder="Rows per page"
                        options={options1}
                        onChange={handleSelectChange}
                        value={selected}
                    />
                          </div>
                          <div>
                            <Button>View Coulumns</Button>
                          </div>
                                        </div>

                    <Page title="User Details">
                        <Card>
                            <DataTable
                                columnContentTypes={[
                                    'text',
                                    'numeric',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                ]}
                                headings={[
                                    'UserId',
                                    'Catalog',
                                    'Shop Domain',
                                    'Shop Email',
                                    'Shop Plan name',
                                    'Updated at',
                                    'Created at',
                                    'Shop myShopify Domain '

                                ]}
                                rows={rows1}
                            />
                        </Card>
                    </Page>
                </div>
            </div>
        </div>
        </>
    )
}
