import React, { useEffect } from 'react'
import { Icon } from '@shopify/polaris';
import axios from 'axios'
import { Page, Card, DataTable } from '@shopify/polaris';
import { ProductsMinor} from '@shopify/polaris-icons';
import {InventoryMajor} from '@shopify/polaris-icons';
import {Select} from '@shopify/polaris';


export default function Dashboard() {
    var temp =[];
    const handler1 = async () => {
        const response1 = await axios(` https://fbapi.sellernext.com/frontend/admin/getAllUsers?activePage=1&count=10`, {
            headers: {
                authorization: sessionStorage.getItem('token')
            }
        })
        console.log(response1)
        response1.data.data.rows.map((d)=>{
            console.log('d =>',d)
            temp.splice(0 , temp.length)
            console.log('TEMP =>',temp)
            temp.push(d.user_id , d.catalog , d.username , d.email , d.shopify_plan , d.updated_at , d.created_at , d.shop_url)
            console.log('TEMP pushed=>',temp)
            console.log('row=>' , rows)
            rows.push([...temp])
            console.log('row-pushed=>',rows)
        })
        // console.log(rows)

    }

    const options = [
        {label: 'Today', value: 'today'},
        {label: 'Yesterday', value: 'yesterday'},
        {label: 'Last 7 days', value: 'lastWeek'},
      ];

    useEffect(() => {
        handler1();
    }, [])

    const rows = [
        [<Select
            label="Date range"
            options={options}
          /> , <Select
          label="Date range"
          options={options}
        /> , <Select
        label="Date range"
        options={options}
      /> , <Select
      label="Date range"
      options={options}
    /> , <Select
    label="Date range"
    options={options}
  /> , <Select
  label="Date range"
  options={options}
/>



        ]
        // ['Emerald Silk Gown', '$875.00', 124689, 140, '$122,500.00'],
        // ['Mauve Cashmere Scarf', '$230.00', 124533, 83, '$19,090.00'],
        // [
        //     'Navy Merino Wool Blazer with khaki chinos and yellow belt',
        //     '$445.00',
        //     124518,
        //     32,
        //     '$14,240.00',
        // ],
    ];
    return (
        <div id="dashboard_products_main_div_id">
            <div id="inner_dashboard_div_1">
                <p className="p1"><Icon
                    source={InventoryMajor}
                    color="base"
                />Dashboard</p>
                <p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                />Products</p><p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                />Grid</p>
            </div>
            <div id="inner_dashboard_div_2">
                <h1 id="h1">
                    Data Grid
                </h1>
                <h2 id="h2">
                    Showing from 0 to 10 of 0 users
                </h2>
                <div>
                    <Page title="User Details">
                        <Card>
                            <DataTable
                                columnContentTypes={[
                                    'text',
                                    'numeric',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                ]}
                                headings={[
                                    'UserId',
                                    'Catalog',
                                    'Shop Domain',
                                    'Shop Email',
                                    'Shop Plan name',
                                    'Updated at',
                                    'Created at',
                                    'Shop myShopify Domain '

                                ]}
                                rows={rows}
                                // totals={['', '', '', 255, '$155,830.00']}
                            />
                        </Card>
                    </Page>
                </div>
            </div>
        </div>
    )
}
