import React, { useCallback, useEffect, useState } from 'react'
import { Icon } from '@shopify/polaris';
import axios from 'axios'
import { Page, Card, DataTable } from '@shopify/polaris';
import { ProductsMinor } from '@shopify/polaris-icons';
import { InventoryMajor } from '@shopify/polaris-icons';
import { Select } from '@shopify/polaris';
import {Button} from '@shopify/polaris';


export default function Dashboard() {
    const[count , setCount]=useState(1)
    var temp = [];
    const [selected, setSelected] = useState(10);
    const options1 = [
        { label: '10', value: '10' },
        { label: '20', value: '20' },
        { label:'30', value: '30' },
        { label:'40', value: '40' },
    ];

    const changepage=(e)=>{
       var x = Number(selected)
       if(x===10){
        if(e.target.innerText ==="Prev"){
            if(count>2){
                setCount(count - 1)
            }
          }
          if(e.target.innerText==="Next"){
            if(count<Math.ceil(24392/10)){
                setCount(count - 1)
            }
          }
       }
       if(x===20){
        if(e.target.innerText ==="Prev"){
            if(count>2){
                setCount(count - 1)
            }
          }
          if(e.target.innerText==="Next"){
            if(count<Math.ceil(24392/20)){
                setCount(count - 1)
            }
          }
       }
       if(x===30){
        if(e.target.innerText ==="Prev"){
            if(count>2){
                setCount(count - 1)
            }
          }
          if(e.target.innerText==="Next"){
            if(count<Math.ceil(24392/30)){
                setCount(count - 1)
            }
          }
       }
       if(x===40){
        if(e.target.innerText ==="Prev"){
            if(count>2){
                setCount(count - 1)
            }
          }
          if(e.target.innerText==="Next"){
            if(count<24392){
                setCount(count - 1)
            }
          }
       }

    }

    const handler1 = async () => {
        const response1 = await axios(` https://fbapi.sellernext.com/frontend/admin/getAllUsers?activePage=${count}&count=${selected}`, {
            headers: {
                authorization: sessionStorage.getItem('token')
            }
        })
        console.log(response1)
        response1.data.data.rows.map((d) => {
            // console.log('d =>', d)
            temp.splice(0, temp.length)
            // console.log('TEMP =>', temp)
            temp.push(d.user_id, d.catalog, d.username, d.email, d.shopify_plan, d.updated_at, d.created_at, d.shop_url)
            // console.log('TEMP pushed=>', temp)
            // console.log('row=>', rows)
            rows.push([...temp])
            // console.log('row-pushed=>', rows)
        })
        // console.log(rows)

    }
    // console.log(selected)
    const options = [
        { label: 'Equals', value: 'today' },
        { label: 'Yesterday', value: 'yesterday' },
        { label: 'Last 7 days', value: 'lastWeek' },
    ];

    const handleSelectChange = useCallback((value) => setSelected(value),  []);

    useEffect(() => {
        handler1();
    }, [])

    const rows = [
        [
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />,
            <Select

                options={options}
            />



        ]

    ];
    return (
        <div id="dashboard_products_main_div_id">
            <div id="inner_dashboard_div_1">
                <p className="p1"><Icon
                    source={InventoryMajor}
                    color="base"
                />Dashboard</p>
                <p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                />Products</p><p className="p1"><Icon
                    source={ProductsMinor}
                    color="base"
                />Grid</p>
            </div>
            <div id="inner_dashboard_div_2">
                <h1 id="h1">
                    Data Grid
                </h1>
                <h2 id="h2">
                    Showing from 0 to 10 of 0 users
                </h2>
                <div>
                    <div id="buttons_and_select_div_id">
                        <div><Button id="btn1" onClick={changepage}>Prev</Button>Results<Button id="btn2" onClick={changepage}>Next</Button></div>
                        <div><Select
                        placeholder="Rows per page"
                        options={options1}
                        onChange={handleSelectChange}
                        value={selected}
                    />
                          </div>
                          <div>
                            <Button>View Coulumns</Button>
                          </div>
                                        </div>

                    <Page title="User Details">
                        <Card>
                            <DataTable
                                columnContentTypes={[
                                    'text',
                                    'numeric',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                    'text',
                                ]}
                                headings={[
                                    'UserId',
                                    'Catalog',
                                    'Shop Domain',
                                    'Shop Email',
                                    'Shop Plan name',
                                    'Updated at',
                                    'Created at',
                                    'Shop myShopify Domain '

                                ]}
                                rows={rows}
                            // totals={['', '', '', 255, '$155,830.00']}
                            />
                        </Card>
                    </Page>
                </div>
            </div>
        </div>
    )
}
